import React, { useState } from 'react';
import './App.css';
import Alert from './components/Alert';
import About from './components/About';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";

const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    errorElement: <ErrorPage />,
  },
  {
    path: "contacts/:contactId",
    element: <Contact />,
  },
]);

function App() {
  const [mode,setmode] = useState("light");
  const [alert,setalert] = useState(null);

  const showAlert = (msg, type)=>{
    setalert({
      msg : msg,
      type : type
    });
    setTimeout(() => {
      setalert(null);
    }, 1500);
  }

  const toggleMode = ()=>{
    if (mode==='light') {
      setmode('dark');
      document.body.style.backgroundColor = "#0b2e4c";
      showAlert("Dark Mode Enabled Successfully","success");
      document.title = 'TextUtils - Dark mode';
    }else{
      setmode('light');
      document.body.style.backgroundColor = "white";
      showAlert("Light Mode Enabled Successfully","success");
      document.title = 'TextUtils - Light mode';
    }
  }

  return (
  <>
    <Navbar title="TextUtils" aboutTitle="About Us" mode={mode} toggleMode={toggleMode} />
    <Alert alert={alert} />
    <div className="container">
    <Routes>
        <Route path="/about" element={<About/>} />
        <Route path="/" element={<TextForm enterText="Enter the text to analyze" mode={mode} showAlert={showAlert} />} />
    </Routes>
    </div>
  </>
  );
}

export default App;
