import React, {useState} from 'react'

export default function TextForm(props) {

    const handleOnClick =(event)=>{
        setText(text.toUpperCase());
    }

    const handleOnchange =(e)=>{
        setText(e.target.value);
    }

    const[text,setText] = useState("Enter text here");

  return (
    <div>
        <h2>{props.enterText}</h2>
        <div className="mb-3">
            <textarea className="form-control" onChange={handleOnchange} placeholder="Enter text here" id="myBox" rows="8"></textarea>
        </div>
        <button className="btn btn-primary" onClick={handleOnClick}>Convert to Upper Case</button>
    </div>
  )
}
