import React from 'react'

export default function Alert(props) {
    const capitalize = (text) =>{
        const lower = text.toLowerCase();
        return lower.charAt(0).toUpperCase() + lower.slice(1);
    }
  return (
    props.alert && 
    <div className={`toast align-items-center text-bg-${props.alert.type} border-0 fade show`} role="alert" aria-live="assertive" aria-atomic="true" bis_skin_checked="1">
      <div className="d-flex" bis_skin_checked="1">
        <div className="toast-body" bis_skin_checked="1">
        <strong>{capitalize(props.alert.type)} </strong>{props.alert.msg}
        </div>
        <button type="button" className="btn-close btn-close-white me-2 m-auto" data-bs-dismiss="toast" aria-label="Close"></button>
      </div>
    </div>       
  )
}
